void SettingsScreenViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &backButton)
    {
        //GoBackToMain
        //When backButton clicked change screen to MainScreen
        //Go to MainScreen with screen transition towards West
        application().gotoMainScreenScreenWipeTransitionWest();
    }
    if (&src == &xAxisButton)
    {
        //GoToXAxisSettings
        //When xAxisButton clicked change screen to XAxisStartScreen
        //Go to XAxisStartScreen with screen transition towards East
        application().gotoXAxisStartScreenScreenWipeTransitionEast();
    }
    if (&src == &yAxisButton)
    {
        //GoToYAxisSettings
        //When yAxisButton clicked change screen to YAxisStartScreen
        //Go to YAxisStartScreen with screen transition towards East
        application().gotoYAxisStartScreenScreenWipeTransitionEast();
    }
    if (&src == &zAxisButton)
    {
        //GoToZAxisSettings
        //When zAxisButton clicked change screen to ZAxisStartScreen
        //Go to ZAxisStartScreen with screen transition towards East
        application().gotoZAxisStartScreenScreenWipeTransitionEast();
    }
    if (&src == &rotationButton)
    {
        //GoToRotationSettings
        //When rotationButton clicked change screen to RotationSpeedScreen
        //Go to RotationSpeedScreen with screen transition towards East
        application().gotoRotationSpeedScreenScreenWipeTransitionEast();
    }
    if (&src == &heaterButton)
    {
        //GoToHeaterSettings
        //When heaterButton clicked change screen to HeaterTemperatureScreen
        //Go to HeaterTemperatureScreen with screen transition towards East
        application().gotoHeaterTemperatureScreenScreenWipeTransitionEast();
    }
}
