void MainScreenViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &settingsButton)
    {
        //GoToSettingsScreen
        //When settingsButton clicked change screen to SettingsScreen
        //Go to SettingsScreen with screen transition towards East
        application().gotoSettingsScreenScreenWipeTransitionEast();
    }
    if (&src == &testsButton)
    {
        //GoToTestsScreen
        //When testsButton clicked change screen to TestsScreen
        //Go to TestsScreen with screen transition towards East
        application().gotoTestsScreenScreenWipeTransitionEast();
    }
    if (&src == &startButton)
    {
        //GoToStartScreen
        //When startButton clicked change screen to StartScreen
        //Go to StartScreen with screen transition towards East
        application().gotoStartScreenScreenWipeTransitionEast();
    }
    if (&src == &calibrateButton)
    {
        //GoToCalibrateScreen
        //When calibrateButton clicked change screen to CalibrateScreen
        //Go to CalibrateScreen with screen transition towards East
        application().gotoCalibrateScreenScreenWipeTransitionEast();
    }
}
